INSERT INTO `phpcms_module` (`name`, `module`, `moduledir`, `moduledomain`, `iscore`, `iscopy`, `isshare`, `version`, `author`, `site`, `email`, `introduce`, `license`, `faq`, `setting`, `disabled`, `publishdate`, `installdate`, `updatedate`) VALUES ('支付', 'pay', 'pay', '', 0, 0, 0, '1.0.0', 'phpcms team', 'http://www.phpcms.cn', 'phpcms@163.com', 'a', 'a', 'a', 'a:4:{s:8:"paytypes";s:204:"现金支付\r\n在线支付\r\n支付宝\r\n财付通支付\r\n贝宝支付\r\n招商银行\r\n中国工商银行\r\n中国农业银行\r\n中国建设银行\r\n中国交通银行\r\n中国银行\r\n邮政储蓄\r\n邮政汇款";s:6:"alipay";s:18:"phpcmscn@gmail.com";s:14:"alipay_service";s:28:"create_digital_goods_trade_p";s:5:"banks";s:9271:"<table cellspacing="0" cellpadding="0" width="95%" align="center" bgcolor="#ffffff" border="0">\r\n    <tbody>\r\n        <tr>\r\n            <td style="LINE-HEIGHT: 150%" valign="top">\r\n            <table cellspacing="0" cellpadding="0" width="98%" border="0">\r\n                <tbody>\r\n                    <tr>\r\n                        <td width="100%" height="50">邮局汇款</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <table cellspacing="1" cellpadding="5" width="98%" bgcolor="#cccccc" border="0">\r\n                <tbody>\r\n                    <tr bgcolor="#efefef">\r\n                        <td colspan="2" height="20"><strong>交费方式</strong></td>\r\n                    </tr>\r\n                    <tr bgcolor="#ffffff">\r\n                        <td align="center" width="20%" height="40"><font color="#cc0000"><strong><font color="#044d95">邮局汇款</font></strong></font></td>\r\n                        <td style="COLOR: #666666; LINE-HEIGHT: 150%" height="40">邮寄地址：西安市高新区高新路枫叶高层35号楼1706室<br />收款单位：陕西玖肆陆陆网络科技有限公司 <br />邮政编码：710075</td>\r\n                    </tr>\r\n                    <tr bgcolor="#eeeeee">\r\n                        <td colspan="2" height="40">&nbsp;请注意：邮局回执上应有邮局日戳</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <table cellspacing="0" cellpadding="0" width="98%" border="0">\r\n                <tbody>\r\n                    <tr>\r\n                        <td width="100%" height="50">银行转帐</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <table cellspacing="1" cellpadding="5" width="98%" bgcolor="#cccccc" border="0">\r\n                <tbody>\r\n                    <tr bgcolor="#efefef">\r\n                        <td align="center" width="20%" height="20">\r\n                        <div align="center"><strong>银 行</strong></div>\r\n                        </td>\r\n                        <td align="center">\r\n                        <div align="center"><strong>帐号</strong></div>\r\n                        </td>\r\n                    </tr>\r\n                    <tr bgcolor="#ffffff">\r\n                        <td style="LINE-HEIGHT: 150%" align="center" width="14%" bgcolor="#ffffff" height="80"><font color="#cc0000"><strong><font color="#044d95">招商银行</font></strong></font></td>\r\n                        <td style="COLOR: #666666; LINE-HEIGHT: 150%">开户银行：招商银行西安分行xxx支行 <br />银行帐号：<strong>xxxxxxxxxxxxx</strong><br />帐户名称：陕西玖肆陆陆网络科技有限公司</td>\r\n                    </tr>\r\n                    <tr bgcolor="#ffffff">\r\n                        <td align="left" colspan="2" height="6">&nbsp;</td>\r\n                    </tr>\r\n                    <tr bgcolor="#ffffff">\r\n                        <td align="center" width="14%" bgcolor="#ffffff" height="80"><font color="#cc0000"><strong><font color="#044d95">招商银行</font></strong></font></td>\r\n                        <td style="COLOR: #666666; LINE-HEIGHT: 150%" align="left">开户银行：招商银行西安分行 <br />银行帐号：<strong>xxxxxxxxxxxxx</strong><br />帐户名称：xxx</td>\r\n                    </tr>\r\n                    <tr bgcolor="#ffffff">\r\n                        <td align="left" colspan="2" height="6">&nbsp;</td>\r\n                    </tr>\r\n                    <tr bgcolor="#ffffff">\r\n                     