DROP TABLE IF EXISTS `phpcms_special_content`;CREATE TABLE IF NOT EXISTS `phpcms_special_content` (  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,  `specialid` smallint(5) unsigned NOT NULL DEFAULT '0',  `title` char(80) NOT NULL,  `style` char(24) NOT NULL,  `typeid` smallint(5) unsigned NOT NULL DEFAULT '0',  `thumb` char(100) NOT NULL,  `keywords` char(40) NOT NULL,  `description` char(255) NOT NULL,  `url` char(100) NOT NULL,  `curl` char(15) NOT NULL,  `listorder` mediumint(8) unsigned NOT NULL DEFAULT '0',  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0',  `username` char(20) NOT NULL,  `inputtime` int(10) unsigned NOT NULL DEFAULT '0',  `updatetime` int(10) unsigned NOT NULL DEFAULT '0',  `searchid` mediumint(8) unsigned NOT NULL DEFAULT '0',  `islink` tinyint(1) unsigned NOT NULL DEFAULT '0',  `isdata` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否有内容',  PRIMARY KEY  (`id`),  KEY `specialid` (`specialid`,`typeid`,`isdata`),  KEY `typeid` (`typeid`,`isdata`)) TYPE=MyISAM ;